import React, { useEffect, useRef } from 'react';
import './MovingLight.css';

const MovingLight = () => {
  const lightRef = useRef(null);

  useEffect(() => {
    const light = lightRef.current;
    if (!light) return;

    let startTime = Date.now();
    const duration = 20000; // 20 seconds for full cycle (slower)

    const animate = () => {
      const elapsed = Date.now() - startTime;
      const progress = (elapsed % duration) / duration;

      // Create a simple circular pattern
      const radius = 150; // Smaller radius for gentler movement
      const x = Math.cos(progress * Math.PI * 2) * radius + 50; // 50% center
      const y = Math.sin(progress * Math.PI * 2) * radius + 50; // Circular motion

      light.style.left = `${x}%`;
      light.style.top = `${y}%`;

      requestAnimationFrame(animate);
    };

    animate();
  }, []);

  return (
    <div className="moving-light" ref={lightRef}>
      <div className="light-glow"></div>
    </div>
  );
};

export default MovingLight;
