import React from 'react';
import './GrassAnimation.css';

const GrassAnimation = () => {
  // Generate many grass blades with varying properties
  const grassBlades = Array.from({ length: 80 }, (_, i) => ({
    id: i,
    left: (i / 80) * 100, // Distribute evenly across width
    height: 20 + Math.random() * 40, // Random height between 20-60px
    width: 1 + Math.random() * 2, // Random width between 1-3px
    delay: Math.random() * 3, // Random animation delay
    duration: 2 + Math.random() * 2, // Random duration between 2-4s
    intensity: 0.5 + Math.random() * 1.5, // Random sway intensity
  }));

  return (
    <div className="grass-container">
      <div className="grass-ground"></div>
      {grassBlades.map((blade) => (
        <div
          key={blade.id}
          className="grass-blade"
          style={{
            left: `${blade.left}%`,
            height: `${blade.height}px`,
            width: `${blade.width}px`,
            animationDelay: `${blade.delay}s`,
            animationDuration: `${blade.duration}s`,
            '--sway-intensity': blade.intensity,
          }}
        />
      ))}
    </div>
  );
};

export default GrassAnimation;
