import React from 'react';
import './Fun.css';

const Fun = () => {
  const funItems = [
    {
      id: 1,
      title: "Gaming Collection",
      description: "My collection of gaming memorabilia and favorite titles",
      image: "/src/assets/images/fun/Nintendo.png"
    },
    {
      id: 2,
      title: "Digital Art",
      description: "Personal creative projects and digital illustrations",
      image: "/src/assets/images/fun/ImagineFX.png"
    },
    {
      id: 3,
      title: "Photography",
      description: "Capturing moments and exploring visual storytelling",
      image: "/src/assets/images/fun/Clouds.jpg"
    },
    {
      id: 4,
      title: "Design Experiments",
      description: "Exploring new techniques and creative approaches",
      image: "/src/assets/images/fun/style.png"
    }
  ];

  return (
    <section id="fun" className="fun">
      <div className="fun-container">
        <h2 className="fun-title">Fun Stuff</h2>
        <p className="fun-subtitle">Beyond work, here's what I enjoy creating and exploring</p>
        <div className="fun-grid">
          {funItems.map((item) => (
            <div key={item.id} className="fun-item">
              <div className="fun-image">
                <img src={item.image} alt={item.title} />
              </div>
              <div className="fun-content">
                <h3 className="fun-item-title">{item.title}</h3>
                <p className="fun-item-description">{item.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Fun;
