import React, { useEffect, useState } from 'react';
import './Hero.css';

const Hero = () => {
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoaded(true);
    }, 100);

    return () => clearTimeout(timer);
  }, []);

  const renderAnimatedText = (text, className) => {
    return text.split('').map((char, index) => (
      <span
        key={index}
        className={`${className}-char ${isLoaded ? 'animate-in' : ''}`}
        style={{ animationDelay: `${index * 0.1}s` }}
      >
        {char === ' ' ? '\u00A0' : char}
      </span>
    ));
  };

  return (
    <section className="hero">
      <div className="hero-content">
        <div className="hero-text">
          <h1 className="hero-name">
            {renderAnimatedText('Kai Solik', 'hero-name')}
          </h1>
          <h2 className="hero-title">
            {renderAnimatedText('UX/UI Designer', 'hero-title')}
          </h2>
        </div>
        <button className="resume-btn">
          Resume
          <div className="arrow-container">
            <svg 
              className="arrow-icon arrow-out" 
              width="16" 
              height="16" 
              viewBox="0 0 24 24" 
              fill="none" 
              stroke="currentColor" 
              strokeWidth="2" 
              strokeLinecap="round" 
              strokeLinejoin="round"
            >
              <path d="M7 17L17 7M17 7H7M17 7V17"/>
            </svg>
            <svg 
              className="arrow-icon arrow-in" 
              width="16" 
              height="16" 
              viewBox="0 0 24 24" 
              fill="none" 
              stroke="currentColor" 
              strokeWidth="2" 
              strokeLinecap="round" 
              strokeLinejoin="round"
            >
              <path d="M7 17L17 7M17 7H7M17 7V17"/>
            </svg>
          </div>
        </button>
      </div>
    </section>
  );
};

export default Hero;
